package com.example.movieexplorer;

import android.os.Bundle;
import android.util.Log;
import android.view.Menu;
import android.view.MenuItem;

import androidx.activity.EdgeToEdge;
import androidx.appcompat.app.AppCompatActivity;
import androidx.appcompat.widget.SearchView;
import androidx.core.app.ShareCompat;
import androidx.core.graphics.Insets;
import androidx.core.view.MenuItemCompat;
import androidx.core.view.ViewCompat;
import androidx.core.view.WindowInsetsCompat;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.example.movieexplorer.adapter.MovieAdapter;
import com.example.movieexplorer.beans.Movie;
import com.example.movieexplorer.service.MovieService;

import java.util.ArrayList;
import java.util.List;

public class ListActivity extends AppCompatActivity {
    private List<Movie> movies;
    private static final String TAG = "Menu Text Change";
    private RecyclerView recyclerView;
    private MovieService service;
    MovieAdapter adapter;

    //TO DO: declarer adapter


    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        EdgeToEdge.enable(this);
        setContentView(R.layout.activity_list);

        ViewCompat.setOnApplyWindowInsetsListener(findViewById(R.id.logo), (v, insets) -> {
            Insets systemBars = insets.getInsets(WindowInsetsCompat.Type.systemBars());
            v.setPadding(systemBars.left, systemBars.top, systemBars.right, systemBars.bottom);
            return insets;
        });
        movies=new ArrayList<>();
        // TO DO : service get instance
        service= MovieService.getInstance();

        init();
        recyclerView=findViewById(R.id.recycleView);
        adapter=new MovieAdapter(this,service.findAll());
        recyclerView.setAdapter(adapter);
        recyclerView.setLayoutManager(new LinearLayoutManager(this));




    }
    public void init() {
        // creer des instances
        Movie movie1 = new Movie("Inception", "https://m.media-amazon.com/images/M/MV5BMjAxMzY3NjcxNF5BMl5BanBnXkFtZTcwNTI5OTM0Mw@@._V1_.jpg", 4.8f, "Science Fiction", "Christopher Nolan", "Leonardo DiCaprio","Inception is a mind-bending sci-fi thriller that explores the concept of dreams within dreams. The film follows Dom Cobb (played by Leonardo DiCaprio), a skilled thief who specializes in the art of \"extraction,\" where he steals valuable secrets from deep within the subconscious during the dream state. To regain his old life, Cobb takes on a seemingly impossible task—planting an idea into someone’s mind, known as \"inception.\" As the layers of dreams unfold, the line between reality and the dream world begins to blur.");
        Movie movie2 = new Movie("The Godfather", "https://m.media-amazon.com/images/M/MV5BYTJkNGQyZDgtZDQ0NC00MDM0LWEzZWQtYzUzZDEwMDljZWNjXkEyXkFqcGc@._V1_.jpg", 4.9f, "Crime", "Francis Ford Coppola", "Marlon Brando"," The Godfather is an iconic crime drama that delves into the life of the powerful Italian-American crime family, the Corleones. Directed by Francis Ford Coppola, the story focuses on Vito Corleone (Marlon Brando), the patriarch of the family, and his youngest son Michael (Al Pacino), who reluctantly gets drawn into the family’s underworld operations. The film captures the complexities of loyalty, power, and the consequences of living a life of organized crime. It’s widely regarded as one of the greatest films in cinematic history.");
        Movie movie3 = new Movie("The Dark Knight", "https://m.media-amazon.com/images/M/MV5BMTMxNTMwODM0NF5BMl5BanBnXkFtZTcwODAyMTk2Mw@@._V1_.jpg", 4.7f, "Action", "Christopher Nolan", "Christian Bale","The Dark Knight is a critically acclaimed action movie in which Batman (Christian Bale) battles against a new and chaotic villain, the Joker (Heath Ledger), who seeks to plunge Gotham City into anarchy. The film showcases Batman's struggle to maintain justice while dealing with the Joker’s unpredictable nature. Christopher Nolan directs a gripping tale of morality, chaos, and heroism, with Heath Ledger delivering a legendary performance as the Joker. The film’s depth, visual effects, and intense performances have made it a modern classic.");
        Movie movie4 = new Movie("Pulp Fiction","data:image/jpeg;base64,"
                , 4.8f, "Crime", "Quentin Tarantino", "John Travolta","Pulp Fiction is a stylized crime drama directed by Quentin Tarantino, known for its nonlinear narrative and eclectic dialogue. The film weaves together several intersecting storylines involving the criminal underworld of Los Angeles. It follows the lives of two hitmen, Vincent Vega (John Travolta) and Jules Winnfield (Samuel L. Jackson), their boss's wife (Uma Thurman), and a washed-up boxer (Bruce Willis). The movie is celebrated for its unique storytelling style, iconic scenes, and memorable soundtrack.");
        Movie movie5 = new Movie("The Shawshank Redemption", "https://images.squarespace-cdn.com/content/v1/5c75dfa97d0c9166551f52b1/9351f4e2-94f9-42e2-81df-003d5fe7b8e0/9964546b0ba1f6e14a6045e34b341f8ca2a3569752c5afed95b89682fcde1a68._RI_V_TTW_.jpg", 4.9f, "Drama", "Frank Darabont", "Tim Robbins","The film tells the story of Andy Dufresne (Tim Robbins), a banker who is wrongfully convicted of murdering his wife and her lover. Sentenced to life in Shawshank State Penitentiary, Andy befriends fellow inmate Red (Morgan Freeman). Through their friendship, Andy maintains hope and seeks redemption despite the harsh realities of prison life. He uses his financial expertise to help the corrupt warden launder money, ultimately devising an elaborate escape plan that highlights the power of hope and resilience.");
        service.create(movie1);

        service.create(movie2);
        service.create(movie3);
        service.create(movie4);
        service.create(movie5);


    }
    public boolean onCreateOptionsMenu(Menu menu) {
        getMenuInflater().inflate(R.menu.my_menu,menu);
        MenuItem menuItem = menu.findItem(R.id.app_bar_search);
        SearchView searchView = (SearchView) menuItem.getActionView();
        searchView.setOnQueryTextListener(new
                                                  SearchView.OnQueryTextListener() {
                                                      @Override
                                                      public boolean onQueryTextSubmit(String query) {
                                                          return true;

                                                      }
                                                      @Override
                                                      public boolean onQueryTextChange(String newText) {
                                                          Log.d(TAG, newText);
                                                          if (adapter != null){
                                                              adapter.getFilter().filter(newText);
                                                          }
                                                          return true;

                                                      }
                                                  });
        return true;
    }
    @Override
    public boolean onOptionsItemSelected(MenuItem item) {
        if (item.getItemId() == R.id.share) {
            String txt = "Movies";
            String mimeType = "text/plain";
            ShareCompat.IntentBuilder
                    .from(this)
                    .setType(mimeType)
                    .setChooserTitle("MOVIES")
                    .setText(txt)
                    .startChooser();
            return true;
        }
        return super.onOptionsItemSelected(item);
    }
}